import React, { useState, useRef, useEffect } from 'react';
import SearchBar from '../components/SearchBar';
import BookCard from '../components/BookCard';
import BookDetailsModal from '../components/BookDetailsModal';
import { searchBooks } from '../api/openLibrary';

export default function Home() {
  const [query, setQuery] = useState('');
  const [filters, setFilters] = useState({ author: '', year: '', language: '' });
  const [results, setResults] = useState([]);
  const [numFound, setNumFound] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1);
  const [selected, setSelected] = useState(null);
  const [noResults, setNoResults] = useState(false);

  // page size used consistently across UI + API
  const PAGE_SIZE = 12;

  // Keep a ref to the last successful results so we can preserve UI when a next-page call transiently fails
  const lastGoodResults = useRef({ results: [], numFound: 0, page: 1 });

  // Reset page when query or filters change (but DO NOT trigger a search automatically)
  useEffect(() => {
    setPage(1);
  }, [query, filters]);

  async function doSearch(qParam, filtersParam, requestedPage = 1) {
    const currentQuery = qParam ?? query;
    const currentFilters = filtersParam ?? filters;
    const safeQuery = typeof currentQuery === 'string' ? currentQuery.trim() : '';

    if (!safeQuery) {
      setError('âš ï¸ Please enter a search term.');
      setResults([]);
      setNoResults(false);
      return;
    }

    setError('');
    setNoResults(false);
    setLoading(true);

    try {
      // Call the API with the consistent PAGE_SIZE
      const data = await searchBooks({
        q: safeQuery,
        author: currentFilters.author?.trim() || '',
        year: currentFilters.year?.trim() || '',
        language: currentFilters.language || '',
        page: requestedPage,
        limit: PAGE_SIZE,
      });

      // If API returned an error field or empty results but numFound is 0 => real no results
      if (data.error) {
        // Keep previous results visible (don't clear them) unless this was the first search
        if (lastGoodResults.current.results.length === 0 || requestedPage === 1) {
          setResults([]);
        }
        setError('âŒ Failed to fetch data (transient). Please retry.');
        setLoading(false);
        return;
      }

      let docs = Array.isArray(data.docs) ? data.docs : [];

      // Client-side language safety: be lenient.
      if (currentFilters.language) {
        const selectedLang = currentFilters.language.toLowerCase();
        docs = docs.filter((book) => {
          // If book lists languages, require match; if it doesn't, keep it (less aggressive).
          if (Array.isArray(book.language) && book.language.length > 0) {
            return book.language.some((lang) =>
              String(lang).toLowerCase().includes(selectedLang)
            );
          }
          // fallback: sometimes API includes `languages` (objects) or none; keep the book if no explicit language
          if (Array.isArray(book.languages) && book.languages.length > 0) {
            // book.languages might be objects like { key: "/languages/eng" }
            return book.languages.some((L) => {
              const s = typeof L === 'string' ? L : (L && L.key) || '';
              return String(s).toLowerCase().includes(selectedLang);
            });
          }
          // No declared language: keep it (prevents false negatives)
          return true;
        });
      }

      // Update UI with new docs (even if docs is empty but numFound==0)
      setResults(docs);
      setNumFound(data.numFound || 0);
      setPage(requestedPage);
      setError('');

      // Save to lastGoodResults only if we have docs (so we don't overwrite good results with empty results)
      if (docs.length > 0) {
        lastGoodResults.current = { results: docs, numFound: data.numFound || 0, page: requestedPage };
      }

      if (docs.length === 0) {
        if ((data.numFound || 0) > 0) {
          // No docs on this page but API says items exist => transient (should be rare since API wrapper retries)
          setError('ðŸŒ No books returned on this page, but results exist. Try retrying the same page.');
        } else {
          // Truly no results
          setNoResults(true);
        }
      } else {
        setNoResults(false);
      }
    } catch (e) {
      console.error('Search error:', e);
      // preserve existing results if any, show friendly error
      if (lastGoodResults.current.results.length > 0) {
        setError('âŒ Temporary network error. Showing last results. Retry to fetch fresh data.');
        setResults(lastGoodResults.current.results);
        setNumFound(lastGoodResults.current.numFound);
        setPage(lastGoodResults.current.page);
      } else {
        setError('âŒ Failed to fetch data. Please check your connection and try again.');
        setResults([]);
        setNumFound(0);
        setPage(1);
      }
    } finally {
      setLoading(false);
    }
  }

  // Determine total pages based on PAGE_SIZE
  const totalPages = Math.max(1, Math.ceil(numFound / PAGE_SIZE));
  const isLastPage = page >= totalPages;

  return (
    <div>
      <SearchBar
        query={query}
        onQueryChange={setQuery}
        filters={filters}
        onFiltersChange={setFilters}
        onSearch={() => doSearch(query, filters, 1)}
        loading={loading}
      />

      {/* Error + Retry (retry the same page) */}
      {error && (
        <div className="notice error">
          {error}
          <button
            className="btn small retry"
            onClick={() => doSearch(query, filters, page)}
            disabled={loading}
          >
            Retry
          </button>
        </div>
      )}

      {/* Loading */}
      {loading && <div className="notice">ðŸ”Ž Searching books...</div>}

      {/* No Results */}
      {noResults && !loading && !error && (
        <div className="notice">ðŸ“­ No results found for this search.</div>
      )}

      {/* Results */}
      {!loading && results.length > 0 && (
        <div className="grid">
          {results.map((b) => (
            <BookCard
              key={`${b.key}_${b.cover_i || ''}`}
              book={b}
              onOpen={() => setSelected(b)}
            />
          ))}
        </div>
      )}

      {/* Pagination */}
      {numFound > 0 && !loading && (
        <div className="pagination">
          <button
            onClick={() => doSearch(query, filters, Math.max(1, page - 1))}
            disabled={loading || page <= 1}
          >
            Prev
          </button>

          <span>
            Page {page} / {totalPages}
          </span>

          <button
            onClick={() => {
              if (!isLastPage) doSearch(query, filters, page + 1);
            }}
            disabled={loading || isLastPage}
          >
            Next
          </button>
        </div>
      )}

      {/* Modal */}
      {selected && <BookDetailsModal book={selected} onClose={() => setSelected(null)} />}
    </div>
  );
}
