import React, { useState } from 'react';

export default function SearchBar({ onSearch, loading, error, noResults }) {
  const [query, setQuery] = useState('');
  const [filters, setFilters] = useState({ author: '', year: '', language: '' });

  return (
    <section className="search-card">
      <form
        onSubmit={(e) => {
          e.preventDefault();
          onSearch(query, filters); // ✅ Send live input values
        }}
        className="search-form"
      >
        <div className="field-group">
          <input
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Search by title, author, ISBN or keywords"
          />
          <button className="btn" disabled={loading}>Search</button>
        </div>

        <div className="filters">
          <input
            placeholder="Author"
            value={filters.author}
            onChange={(e) => setFilters({ ...filters, author: e.target.value })}
          />
          <input
            placeholder="Year"
            value={filters.year}
            onChange={(e) => setFilters({ ...filters, year: e.target.value })}
          />
          <select
            value={filters.language}
            onChange={(e) => setFilters({ ...filters, language: e.target.value })}
          >
            <option value="">All Languages</option>
            <option value="ENG">English (ENG)</option>
            <option value="FRE">French (FRE)</option>
            <option value="SPA">Spanish (SPA)</option>
            <option value="GER">German (GER)</option>
            <option value="HIN">Hindi (HIN)</option>
            <option value="TEL">Telugu (TEL)</option>
            <option value="CHI">Chinese (CHI)</option>
            <option value="JPN">Japanese (JPN)</option>
            <option value="RUS">Russian (RUS)</option>
          </select>

          <button
            type="button"
            className="btn small"
            onClick={() => setFilters({ author: '', year: '', language: '' })}
          >
            Clear
          </button>
        </div>
      </form>

      {error && <p className="error-message">⚠️ {error}</p>}
      {noResults && !error && <p className="no-results">No results found.</p>}
    </section>
  );
}
