import React, { useState } from 'react';
import SearchBar from '../components/SearchBar';
import BookCard from '../components/BookCard';
import BookDetailsModal from '../components/BookDetailsModal';
import { searchBooks } from '../api/openLibrary';

export default function Home(){
  const [query, setQuery] = useState('');
  const [filters, setFilters] = useState({ author: '', year: '',language: '' });
  const [results, setResults] = useState([]);
  const [numFound, setNumFound] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1);
  const [selected, setSelected] = useState(null);

  async function doSearch(p = 1){
    if (!query.trim()) {
      setError('Please enter a search term.');
      return;
    }
    setError('');
    setLoading(true);
    try {
      const data = await searchBooks({
        q: query,
        author: filters.author,
        year: filters.year,
        language: filters.language,
        page: p,
      });

      let docs = data.docs || [];

    // 🌍 Apply robust client-side language filter
      if (filters.language) {
        docs = docs.filter(
          (book) =>
            Array.isArray(book.language) &&
            book.language.some(
              (lang) =>
                lang.toLowerCase().includes(filters.language.toLowerCase())
            )
        );
      }

      // ✅ Update results before checking for empty results
      setResults(docs);
      setNumFound(data.numFound || 0);
      setPage(p);

      // Only show error if there are truly no docs
      if (docs.length === 0) {
        setError('No results found for this language or filters.');
      }
    } catch (e) {
      setError(e.message || 'Failed to fetch data.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div>
      <SearchBar
        query={query}
        onQueryChange={setQuery}
        filters={filters}
        onFiltersChange={setFilters}
        onSearch={() => doSearch(1)}
      />

      {error && <div className="notice error">{error}</div>}
      {!error && !loading && results.length === 0 && query.trim() && (
        <div className="notice">No results found.</div>
      )}
      {loading && <div className="notice">Loading…</div>}

      <div className="grid">
        {results.map(b => (
          <BookCard key={`${b.key}_${b.cover_i || ''}`} book={b} onOpen={() => setSelected(b)} />
        ))}
      </div>

      {numFound > results.length && (
        <div className="pagination">
          <button onClick={() => doSearch(page - 1)} disabled={page <= 1}>Prev</button>
          <span>Page {page}</span>
          <button onClick={() => doSearch(page + 1)}>Next</button>
        </div>
      )}

      {selected && (
        <BookDetailsModal book={selected} onClose={() => setSelected(null)} />
      )}
    </div>
  );
}
