import React from 'react';

export default function SearchBar({
  query,
  onQueryChange,
  filters,
  onFiltersChange,
  onSearch,
  loading,
  error,
  noResults
}) {
  return (
    <section className="search-card">
      <form
        onSubmit={(e) => {
          e.preventDefault();

          // ‚úÖ Collect all current values directly from form
          const formValues = {
            q: e.target.elements.query.value,
            author: e.target.elements.author.value,
            year: e.target.elements.year.value,
            language: e.target.elements.language.value,
          };

          // ‚úÖ Trigger parent search instantly
          onSearch(formValues);
        }}
        className="search-form"
      >
        <div className="field-group">
          <input
            name="query"
            value={query}
            onChange={(e) => onQueryChange(e.target.value)}
            placeholder="Search by title, author, ISBN or keywords"
          />
          <button className="btn" disabled={loading}>
            {loading ? 'Searching...' : 'Search'}
          </button>
        </div>

        <div className="filters">
          {/* ‚úçÔ∏è Author Filter */}
          <input
            name="author"
            placeholder="Author"
            value={filters.author}
            onChange={(e) => onFiltersChange({ ...filters, author: e.target.value })}
          />

          {/* üìÖ Year Filter */}
          <input
            name="year"
            placeholder="Year"
            value={filters.year}
            onChange={(e) => onFiltersChange({ ...filters, year: e.target.value })}
          />

          {/* üåç Language Filter */}
          <select
            name="language"
            value={filters.language}
            onChange={(e) => onFiltersChange({ ...filters, language: e.target.value })}
          >
            <option value="">All Languages</option>
            <option value="ENG">English (ENG)</option>
            <option value="FRE">French (FRE)</option>
            <option value="SPA">Spanish (SPA)</option>
            <option value="GER">German (GER)</option>
            <option value="HIN">Hindi (HIN)</option>
            <option value="TEL">Telugu (TEL)</option>
            <option value="CHI">Chinese (CHI)</option>
            <option value="JPN">Japanese (JPN)</option>
            <option value="RUS">Russian (RUS)</option>
            <option value="TAM">Tamil (TAM)</option>
          </select>

          {/* ‚ùå Clear Filters */}
          <button
            type="button"
            className="btn small"
            onClick={() => {
              onQueryChange('');
              onFiltersChange({ author: '', year: '', language: '' });
            }}
          >
            Clear
          </button>
        </div>
      </form>

      {/* ‚ö†Ô∏è Error Message */}
      {error && <p className="error-message">‚ö†Ô∏è {error}</p>}

      {/* üì≠ No Results Message */}
      {noResults && !error && (
        <p className="no-results">üì≠ No results found for this search.</p>
      )}
    </section>
  );
}
