import React, { useState, useRef, useEffect } from 'react';
import SearchBar from '../components/SearchBar';
import BookCard from '../components/BookCard';
import BookDetailsModal from '../components/BookDetailsModal';
import { searchBooks } from '../api/openLibrary';

export default function Home() {
  const [query, setQuery] = useState('');
  const [filters, setFilters] = useState({ author: '', year: '', language: '' });
  const [results, setResults] = useState([]);
  const [numFound, setNumFound] = useState(0);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [noResults, setNoResults] = useState(false);
  const [selected, setSelected] = useState(null);

  const PAGE_SIZE = 12;
  const lastGoodResults = useRef({ results: [], numFound: 0, page: 1 });
  const activeRequestId = useRef(0);

  useEffect(() => {
    if (query.trim()) {
      doSearch(1);
    }
  }, [filters]);

  async function doSearch(requestedPage = 1, manualRetry = false) {
    const safeQuery = query.trim();
    if (!safeQuery) {
      setError('‚ö†Ô∏è Please enter a search term.');
      setResults([]);
      setNoResults(false);
      return;
    }

    setLoading(true);
    setError('');
    setNoResults(false);

    const reqId = ++activeRequestId.current;

    try {
      const data = await searchBooks({
        q: safeQuery,
        author: filters.author.trim(),
        year: filters.year.trim(),
        language: filters.language,
        page: requestedPage,
        limit: PAGE_SIZE,
      });

      if (reqId !== activeRequestId.current) return;

      // Defensive check for missing or invalid API responses
      if (!data || !Array.isArray(data.docs)) {
        throw new Error('Invalid API response');
      }

      let docs = data.docs;

      // Apply language filter if selected
      if (filters.language) {
        const selectedLang = filters.language.toLowerCase();
        docs = docs.filter((b) =>
          Array.isArray(b.language)
            ? b.language.some((lang) =>
                String(lang).toLowerCase().includes(selectedLang)
              )
            : true
        );
      }

      // Handle empty results for current page
      if (docs.length === 0) {
        if ((data.numFound || 0) > 0 && !manualRetry) {
          // ü©π Try next page automatically if current page empty
          console.warn(`Page ${requestedPage} empty ‚Äî trying next page...`);
          return doSearch(requestedPage + 1, true);
        } else {
          setResults([]);
          setNumFound(data.numFound || 0);
          setError('‚ö†Ô∏è No books found on this page.');
          return;
        }
      }

      // ‚úÖ Success: update UI and remember last good page
      setResults(docs);
      setNumFound(data.numFound || 0);
      setPage(requestedPage);
      lastGoodResults.current = {
        results: docs,
        numFound: data.numFound || 0,
        page: requestedPage,
      };
    } catch (err) {
      console.error('Search failed:', err);

      // Fallback to previous good state
      if (lastGoodResults.current.results.length > 0 && !manualRetry) {
        setError(`‚ö†Ô∏è Network issue. Showing last known results (Page ${lastGoodResults.current.page}).`);
        setResults(lastGoodResults.current.results);
        setNumFound(lastGoodResults.current.numFound);
        setPage(lastGoodResults.current.page);
      } else {
        setError(`‚ùå Failed to fetch page ${requestedPage}. Please retry.`);
        setResults([]);
      }
    } finally {
      if (reqId === activeRequestId.current) setLoading(false);
    }
  }

  const totalPages = Math.max(1, Math.ceil(numFound / PAGE_SIZE));
  const isFirstPage = page <= 1;
  const isLastPage = page >= totalPages;

  return (
    <div className="home-page">
      <SearchBar
        query={query}
        onQueryChange={setQuery}
        filters={filters}
        onFiltersChange={setFilters}
        onSearch={() => doSearch(1)}
        loading={loading}
      />

      {/* Error Notice with Retry Button */}
      {error && (
        <div className="notice error">
          {error}
          <button
            className="btn small retry"
            onClick={() => doSearch(page, true)}
            disabled={loading}
            style={{ marginLeft: '10px' }}
          >
            üîÅ Retry Page {page}
          </button>
        </div>
      )}

      {/* Loading Notice */}
      {loading && <div className="notice">üîé Searching books (Page {page})...</div>}

      {/* No Results */}
      {noResults && !loading && (
        <div className="notice">
          üì≠ No results found.
          <button
            className="btn small retry"
            onClick={() => doSearch(1, true)}
            disabled={loading}
            style={{ marginLeft: '10px' }}
          >
            üîÅ Retry
          </button>
        </div>
      )}

      {/* Book Grid */}
      {!loading && results.length > 0 && (
        <div className="grid">
          {results.map((b) => (
            <BookCard
              key={`${b.key}_${b.cover_i || ''}`}
              book={b}
              onOpen={() => setSelected(b)}
            />
          ))}
        </div>
      )}

      {/* Pagination */}
      {numFound > 0 && !loading && (
        <div className="pagination">
          <button onClick={() => doSearch(page - 1)} disabled={isFirstPage || loading}>
            ‚óÄ Prev
          </button>
          <span>
            Page {page} / {totalPages}
          </span>
          <button onClick={() => doSearch(page + 1)} disabled={isLastPage || loading}>
            ‚ñ∂ Next
          </button>
        </div>
      )}

      {/* Book Modal */}
      {selected && <BookDetailsModal book={selected} onClose={() => setSelected(null)} />}
    </div>
  );
}
