import React, { useState } from 'react';
import SearchBar from '../components/SearchBar';
import BookCard from '../components/BookCard';
import BookDetailsModal from '../components/BookDetailsModal';
import { searchBooks } from '../api/openLibrary';

export default function Home() {
  const [query, setQuery] = useState('');
  const [filters, setFilters] = useState({ author: '', year: '', language: '' });
  const [results, setResults] = useState([]);
  const [numFound, setNumFound] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1);
  const [selected, setSelected] = useState(null);
  const [noResults, setNoResults] = useState(false);

  async function doSearch(currentQuery = query, currentFilters = filters, p = 1) {
    // ‚úÖ Ensure currentQuery is a string before trimming
    const safeQuery = typeof currentQuery === 'string' ? currentQuery.trim() : '';

    if (!safeQuery) {
      setError('Please enter a search term.');
      setResults([]);
      setNoResults(false);
      return;
    }

    setError('');
    setNoResults(false);
    setLoading(true);

    try {
      const data = await searchBooks({
        q: safeQuery,
        author: currentFilters.author?.trim() || '',
        year: currentFilters.year?.trim() || '',
        language: currentFilters.language || '',
        page: p,
      });


      let docs = data.docs || [];

      // üåç Apply language filter (client-side fallback)
      if (currentFilters.language) {
        const selectedLang = currentFilters.language.toLowerCase();
        docs = docs.filter(
          (book) =>
            Array.isArray(book.language) &&
            book.language.some((lang) => lang.toLowerCase().includes(selectedLang))
        );
      }

      setResults(docs);
      setNumFound(data.numFound || 0);
      setPage(p);

      if (docs.length === 0) {
        if (currentFilters.language) {
          setError(`üåç No books found in ${currentFilters.language}. Try another language or remove the filter.`);
        } else {
          setNoResults(true);}
      }
    } catch (e) {
      console.error('Search error:', e);
      setError('‚ùå Failed to fetch data. Please check your connection and try again.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div>
      <SearchBar
        query={query}
        onQueryChange={setQuery}
        filters={filters}
        onFiltersChange={setFilters}
        // ‚úÖ Always pass clean current state
        onSearch={doSearch}
        loading={loading}
      />

      {error && (
        <div className="notice error">
          {error}
          <button className="btn small retry" onClick={() => doSearch(query, filters, 1)}>
            Retry
          </button>
        </div>
      )}

      {/* üîç Loading State */}
      {loading && <div className="notice">üîé Searching books...</div>}

      {/* üìò No Results */}
      {noResults && !loading && !error && (
        <div className="notice">üì≠ No results found for this search.</div>
      )}

      {/* üìö Book Grid */}
      {!loading && !error && results.length > 0 && (
        <div className="grid">
          {results.map((b) => (
            <BookCard
              key={`${b.key}_${b.cover_i || ''}`}
              book={b}
              onOpen={() => setSelected(b)}
            />
          ))}
        </div>
      )}

      {numFound > results.length && !loading && (
        <div className="pagination">
          <button onClick={() => doSearch(query, filters, page - 1)} disabled={page <= 1}>
            Prev
          </button>
          <span>Page {page}</span>
          <button onClick={() => doSearch(query, filters, page + 1)}>
            Next
          </button>
        </div>
      )}

      {selected && (
        <BookDetailsModal book={selected} onClose={() => setSelected(null)} />
      )}
    </div>
  );
}
