import React from 'react';

export default function SearchBar({ query, onQueryChange, filters, onFiltersChange, onSearch }) {
  return (
    <section className="search-card">
      <form
        onSubmit={(e) => {
          e.preventDefault();
          onSearch();
        }}
        className="search-form"
      >
        <div className="field-group">
          <input
            value={query}
            onChange={(e) => onQueryChange(e.target.value)}
            placeholder="Search by title, author, ISBN or keywords"
          />
          <button className="btn">Search</button>
        </div>

        <div className="filters">
          {/* Author Filter */}
          <input
            placeholder="Author"
            value={filters.author}
            onChange={(e) => onFiltersChange({ ...filters, author: e.target.value })}
          />

          {/* Year Filter */}
          <input
            placeholder="Year"
            value={filters.year}
            onChange={(e) => onFiltersChange({ ...filters, year: e.target.value })}
          />

          {/* üåç New Language Filter */}
          <select
            value={filters.language}
            onChange={(e) => onFiltersChange({ ...filters, language: e.target.value })}
          >
            <option value="">All Languages</option>
            <option value="eng">English</option>
            <option value="fre">French</option>
            <option value="spa">Spanish</option>
            <option value="ger">German</option>
            <option value="hin">Hindi</option>
          </select>

          <button
            type="button"
            className="btn small"
            onClick={() => {
              onFiltersChange({ author: '', year: '', language: '' });
            }}
          >
            Clear
          </button>
        </div>
      </form>
    </section>
  );
}
