import React, { useState, useRef, useEffect } from 'react';
import SearchBar from '../components/SearchBar';
import BookCard from '../components/BookCard';
import BookDetailsModal from '../components/BookDetailsModal';
import { searchBooks } from '../api/openLibrary';

export default function Home() {
  const [query, setQuery] = useState('');
  const [filters, setFilters] = useState({ author: '', year: '', language: '' });
  const [results, setResults] = useState([]);
  const [numFound, setNumFound] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1);
  const [selected, setSelected] = useState(null);
  const [noResults, setNoResults] = useState(false);

  // ‚úÖ Always keep the *latest* values ready
  const latestValues = useRef({ query: '', filters: {} });
  useEffect(() => {
    latestValues.current = { query, filters };
  }, [query, filters]);

  async function doSearch(qParam, filtersParam, p = 1) {
    // ‚úÖ Always get the very latest state
    const currentQuery = qParam ?? latestValues.current.query;
    const currentFilters = filtersParam ?? latestValues.current.filters;

    const safeQuery = typeof currentQuery === 'string' ? currentQuery.trim() : '';
    if (!safeQuery) {
      setError('‚ö†Ô∏è Please enter a search term.');
      setResults([]);
      setNoResults(false);
      return;
    }

    setError('');
    setNoResults(false);
    setLoading(true);

    try {
      const data = await searchBooks({
        q: safeQuery,
        author: currentFilters.author?.trim() || '',
        year: currentFilters.year?.trim() || '',
        language: currentFilters.language || '',
        page: p,
      });

      let docs = data.docs || [];

      // üåç Apply language filter fallback
      if (currentFilters.language) {
        const selectedLang = currentFilters.language.toLowerCase();
        docs = docs.filter(
          (book) =>
            Array.isArray(book.language) &&
            book.language.some((lang) => lang.toLowerCase().includes(selectedLang))
        );
      }

      setResults(docs);
      setNumFound(data.numFound || 0);
      setPage(p);

      if (docs.length === 0) {
        if (currentFilters.language) {
          setError(
            `üåç No books found in ${currentFilters.language}. Try another language or remove the filter.`
          );
        } else {
          setNoResults(true);
        }
      } else {
        setError('');
      }
    } catch (e) {
      console.error('Search error:', e);
      setError('‚ùå Failed to fetch data. Please check your connection and try again.');
    } finally {
      setLoading(false);
    }
  }

  const handlePrevPage = () => {
    if (page > 1) {
      doSearch(latestValues.current.query, latestValues.current.filters, page - 1);
    }
  };

  const handleNextPage = () => {
    const totalPages = Math.ceil(numFound / 100); // ‚úÖ calculate total pages safely
    if (page < totalPages) {
      doSearch(latestValues.current.query, latestValues.current.filters, page + 1);
    }
  };

  return (
    <div>
      <SearchBar
        query={query}
        onQueryChange={setQuery}
        filters={filters}
        onFiltersChange={setFilters}
        // ‚úÖ Always pulls latest values from ref
        onSearch={() => doSearch(latestValues.current.query, latestValues.current.filters, 1)}
        loading={loading}
      />

      {/* ‚ö†Ô∏è Error message + Retry */}
      {error && (
        <div className="notice error">
          {error}
          <button
            className="btn small retry"
            onClick={() =>
              doSearch(latestValues.current.query, latestValues.current.filters, 1)
            }
          >
            Retry
          </button>
        </div>
      )}

      {/* üîç Loading */}
      {loading && <div className="notice">üîé Searching books...</div>}

      {/* üì≠ No Results */}
      {noResults && !loading && !error && (
        <div className="notice">üì≠ No results found for this search.</div>
      )}

      {/* üìö Results */}
      {!loading && !error && results.length > 0 && (
        <div className="grid">
          {results.map((b) => (
            <BookCard
              key={`${b.key}_${b.cover_i || ''}`}
              book={b}
              onOpen={() => setSelected(b)}
            />
          ))}
        </div>
      )}

      {/* ‚è© Pagination */}
      {numFound > results.length && !loading && (
        <div className="pagination">
          <button onClick={handlePrevPage} disabled={page <= 1}>
            Prev
          </button>
          <span>Page {page}</span>
          <button
            onClick={handleNextPage}
            disabled={page * results.length >= numFound} // ‚úÖ disable Next on last page
          >
            Next
          </button>
        </div>
      )}

      {/* üìò Modal */}
      {selected && (
        <BookDetailsModal book={selected} onClose={() => setSelected(null)} />
      )}
    </div>
  );
}
