import React from 'react';
import { coverUrl } from '../api/openLibrary';
import useLocalStorage from '../hooks/useLocalStorage';

export default function BookCard({ book, onOpen }){
  const [favorites, setFavorites] = useLocalStorage('bf_favorites', []);

  const id = book.key || (book.cover_edition_key ? '/books/' + book.cover_edition_key : book.title);
  const isFav = favorites.some(f => f.key === id);

  function toggle(){
    const small = { key: id, title: book.title, authors: book.author_name || [], cover_i: book.cover_i || null, year: book.first_publish_year };
    if(isFav) setFavorites(favorites.filter(f => f.key !== id));
    else setFavorites([small, ...favorites]);
  }

  return (
    <article className="card">
      <div className="cover">
        {book.cover_i ? <img src={coverUrl(book.cover_i)} alt={`Cover of ${book.title}`} /> : <div className="placeholder">No cover</div>}
      </div>
      <div className="info">
        <h3>{book.title}</h3>
        <p className="muted">{(book.author_name || []).slice(0,3).join(', ')}</p>
        <p className="muted small">{book.first_publish_year || ''}</p>
        <div className="card-actions">
          <button className="btn small" onClick={() => onOpen && onOpen(book)}>Details</button>
          <button className={`btn small ${isFav ? 'active' : ''}`} onClick={toggle}>{isFav ? 'Unfav' : 'Fav'}</button>
        </div>
      </div>
    </article>
  );
}
