import React from 'react';
import useLocalStorage from '../hooks/useLocalStorage';
import BookCard from '../components/BookCard';

export default function Favorites(){
  const [favorites] = useLocalStorage('bf_favorites', []);

  return (
    <div>
      <h2>Favorites</h2>
      {favorites.length === 0 ? (
        <div className="notice">No favorites yet. Add some from the search page.</div>
      ) : (
        <div className="grid">
          {favorites.map(f => (
            <BookCard key={f.key} book={f} isFav initialFav onOpen={() => {}} />
          ))}
        </div>
      )}
    </div>
  );
}
