import React, { useState, useRef } from 'react';
import SearchBar from '../components/SearchBar';
import BookCard from '../components/BookCard';
import BookDetailsModal from '../components/BookDetailsModal';
import { searchBooks } from '../api/openLibrary';

export default function Home() {
  const [query, setQuery] = useState('');
  const [filters, setFilters] = useState({ author: '', year: '', language: '' });
  const [results, setResults] = useState([]);
  const [numFound, setNumFound] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1);
  const [selected, setSelected] = useState(null);
  const [noResults, setNoResults] = useState(false);

  // ✅ Store the latest query and filters for retry and pagination
  const latestSearch = useRef({ q: '', filters: {} });

  async function doSearch(qParam, filtersParam, p = 1) {
    // ✅ Always get the latest values from parameters or state
    const currentQuery = qParam ?? query;
    const currentFilters = filtersParam ?? filters;

    const safeQuery = typeof currentQuery === 'string' ? currentQuery.trim() : '';
    if (!safeQuery) {
      setError('⚠️ Please enter a search term.');
      setResults([]);
      setNoResults(false);
      return;
    }

    latestSearch.current = { q: safeQuery, filters: currentFilters };

    setError('');
    setNoResults(false);
    setLoading(true);

    try {
      const data = await searchBooks({
        q: safeQuery,
        author: currentFilters.author?.trim() || '',
        year: currentFilters.year?.trim() || '',
        language: currentFilters.language || '',
        page: p,
      });

      let docs = data.docs || [];

      // 🌍 Apply language filter (client-side fallback)
      if (currentFilters.language) {
        const selectedLang = currentFilters.language.toLowerCase();
        docs = docs.filter(
          (book) =>
            Array.isArray(book.language) &&
            book.language.some((lang) => lang.toLowerCase().includes(selectedLang))
        );
      }

      setResults(docs);
      setNumFound(data.numFound || 0);
      setPage(p);

      if (docs.length === 0) {
        if (currentFilters.language) {
          setError(
            `🌍 No books found in ${currentFilters.language}. Try another language or remove the filter.`
          );
        } else {
          setNoResults(true);
        }
      } else {
        setError('');
      }
    } catch (e) {
      console.error('Search error:', e);
      setError('❌ Failed to fetch data. Please check your connection and try again.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div>
      <SearchBar
        query={query}
        onQueryChange={setQuery}
        filters={filters}
        onFiltersChange={setFilters}
        // ✅ Directly call doSearch with latest state (no delay)
        onSearch={() => doSearch(query, filters, 1)}
        loading={loading}
      />

      {/* ⚠️ Error message + Retry */}
      {error && (
        <div className="notice error">
          {error}
          <button
            className="btn small retry"
            onClick={() => {
              if (latestSearch.current.q) {
                doSearch(latestSearch.current.q, latestSearch.current.filters, 1);
              } else {
                doSearch(query, filters, 1);
              }
            }}
          >
            Retry
          </button>
        </div>
      )}

      {/* 🔍 Loading */}
      {loading && <div className="notice">🔎 Searching books...</div>}

      {/* 📭 No Results */}
      {noResults && !loading && !error && (
        <div className="notice">📭 No results found for this search.</div>
      )}

      {/* 📚 Results */}
      {!loading && !error && results.length > 0 && (
        <div className="grid">
          {results.map((b) => (
            <BookCard
              key={`${b.key}_${b.cover_i || ''}`}
              book={b}
              onOpen={() => setSelected(b)}
            />
          ))}
        </div>
      )}

      {/* ⏩ Pagination */}
      {numFound > results.length && !loading && (
        <div className="pagination">
          <button
            onClick={() =>
              doSearch(latestSearch.current.q, latestSearch.current.filters, page - 1)
            }
            disabled={page <= 1}
          >
            Prev
          </button>
          <span>Page {page}</span>
          <button
            onClick={() =>
              doSearch(latestSearch.current.q, latestSearch.current.filters, page + 1)
            }
          >
            Next
          </button>
        </div>
      )}

      {/* 📘 Modal */}
      {selected && (
        <BookDetailsModal book={selected} onClose={() => setSelected(null)} />
      )}
    </div>
  );
}
