import React from 'react';

export default function SearchBar({ query, onQueryChange, filters, onFiltersChange, onSearch }){
  return (
    <section className="search-card">
      <form onSubmit={(e) => { e.preventDefault(); onSearch() }} className="search-form">
        <div className="field-group">
          <input value={query} onChange={e => onQueryChange(e.target.value)} placeholder="Search by title, author, ISBN or keywords" />
          <button className="btn">Search</button>
        </div>

        <div className="filters">
          <input placeholder="Author" value={filters.author} onChange={e => onFiltersChange({ ...filters, author: e.target.value })} />
          <input placeholder="Year" value={filters.year} onChange={e => onFiltersChange({ ...filters, year: e.target.value })} />
          <button type="button" className="btn small" onClick={() => { onFiltersChange({ author: '', year: '' }) }}>Clear</button>
        </div>
      </form>
    </section>
  );
}
