// src/pages/Home.js
import React, { useState, useRef, useEffect } from 'react';
import SearchBar from '../components/SearchBar';
import BookCard from '../components/BookCard';
import BookDetailsModal from '../components/BookDetailsModal';
import { searchBooks } from '../api/openLibrary';

export default function Home() {
  const [query, setQuery] = useState('');
  const [filters, setFilters] = useState({ author: '', year: '', language: '' });
  const [results, setResults] = useState([]);
  const [numFound, setNumFound] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1);
  const [selected, setSelected] = useState(null);
  const [noResults, setNoResults] = useState(false);

  const PAGE_SIZE = 12;
  const lastGoodResults = useRef({ results: [], numFound: 0, page: 1 });

  // Reset to page 1 whenever query or filters change
  useEffect(() => {
    setPage(1);
  }, [query, filters]);

  async function doSearch(qParam, filtersParam, requestedPage = 1) {
    const currentQuery = qParam ?? query;
    const currentFilters = filtersParam ?? filters;
    const safeQuery = typeof currentQuery === 'string' ? currentQuery.trim() : '';

    if (!safeQuery) {
      setError('‚ö†Ô∏è Please enter a search term.');
      setResults([]);
      setNoResults(false);
      return;
    }

    setError('');
    setNoResults(false);
    setLoading(true);

    try {
      const data = await searchBooks({
        q: safeQuery,
        author: currentFilters.author?.trim() || '',
        year: currentFilters.year?.trim() || '',
        language: currentFilters.language || '',
        page: requestedPage,
        limit: PAGE_SIZE,
      });

      let docs = Array.isArray(data.docs) ? data.docs : [];

      // Optional client-side language filter
      if (currentFilters.language) {
        const selectedLang = currentFilters.language.toLowerCase();
        docs = docs.filter((book) =>
          Array.isArray(book.language)
            ? book.language.some((lang) =>
                String(lang).toLowerCase().includes(selectedLang)
              )
            : true
        );
      }

      setResults(docs);
      setNumFound(data.numFound || 0);
      setPage(requestedPage);

      if (docs.length > 0) {
        lastGoodResults.current = {
          results: docs,
          numFound: data.numFound || 0,
          page: requestedPage,
        };
        setNoResults(false);
        setError('');
      } else if ((data.numFound || 0) > 0) {
        setError('üåç No books returned on this page, but results exist. Try another page.');
      } else {
        setNoResults(true);
      }
    } catch (e) {
      console.error('Search error:', e);
      if (lastGoodResults.current.results.length > 0) {
        setError('‚ö†Ô∏è Network error. Showing last results. Retry to refresh.');
        setResults(lastGoodResults.current.results);
        setNumFound(lastGoodResults.current.numFound);
        setPage(lastGoodResults.current.page);
      } else {
        setError('‚ùå Failed to fetch data. Please check your connection and retry.');
        setResults([]);
        setNumFound(0);
        setPage(1);
      }
    } finally {
      setLoading(false);
    }
  }

  // Pagination helpers
  const totalPages = Math.max(1, Math.ceil(numFound / PAGE_SIZE));
  const isFirstPage = page <= 1;
  const isLastPage = page >= totalPages;

  return (
    <div className="home-page">
      {/* üîç Search Bar */}
      <SearchBar
        query={query}
        onQueryChange={setQuery}
        filters={filters}
        onFiltersChange={setFilters}
        onSearch={() => doSearch(query, filters, 1)}
        loading={loading}
      />

      {/* ‚ö†Ô∏è Error Message */}
      {error && (
        <div className="notice error">
          {error}
          <button
            className="btn small retry"
            onClick={() => doSearch(query, filters, page)}
            disabled={loading}
          >
            üîÅ Retry
          </button>
        </div>
      )}

      {/* üîé Loading */}
      {loading && <div className="notice">üîé Searching books...</div>}

      {/* üì≠ No Results */}
      {noResults && !loading && !error && (
        <div className="notice">
          üì≠ No results found.
          <button
            className="btn small retry"
            onClick={() => doSearch(query, filters, 1)}
            disabled={loading}
            style={{ marginLeft: '10px' }}
          >
            üîÅ Retry
          </button>
        </div>
      )}

      {/* üìö Results Grid */}
      {!loading && results.length > 0 && (
        <div className="grid">
          {results.map((b) => (
            <BookCard
              key={`${b.key}_${b.cover_i || ''}`}
              book={b}
              onOpen={() => setSelected(b)}
            />
          ))}
        </div>
      )}

      {/* ‚è© Pagination */}
      {numFound > 0 && !loading && (
        <div className="pagination">
          <button
            onClick={() => doSearch(query, filters, page - 1)}
            disabled={isFirstPage || loading}
          >
            ‚óÄ Prev
          </button>

          <span>
            Page {page} / {totalPages}
          </span>

          <button
            onClick={() => doSearch(query, filters, page + 1)}
            disabled={isLastPage || loading}
          >
            ‚ñ∂ Next
          </button>
        </div>
      )}

      {/* üìò Modal */}
      {selected && (
        <BookDetailsModal book={selected} onClose={() => setSelected(null)} />
      )}
    </div>
  );
}
