const BASE = 'https://openlibrary.org';

export async function searchBooks({ q, author, year, page = 1, limit = 12 }){
  if(!q || !q.trim()) return { docs: [], numFound: 0 };
  const params = new URLSearchParams();
  params.set('q', q);
  if(author) params.set('author', author);
  if(year) params.set('first_publish_year', year);
  params.set('page', page);
  params.set('limit', limit);

  const url = `${BASE}/search.json?${params.toString()}`;
  const res = await fetch(url);
  if(!res.ok) throw new Error('API error ' + res.status);
  const data = await res.json();
  return data;
}

export function coverUrl(id, size = 'M'){
  if(!id) return null;
  return `https://covers.openlibrary.org/b/id/${id}-${size}.jpg`;
}

export function openLibraryUrl(key){
  if(!key) return 'https://openlibrary.org';
  return `${BASE}${key}`;
}
