import React, { useState, useRef, useEffect } from 'react';
import SearchBar from '../components/SearchBar';
import BookCard from '../components/BookCard';
import BookDetailsModal from '../components/BookDetailsModal';
import { searchBooks } from '../api/openLibrary';

export default function Home() {
  const [query, setQuery] = useState('');
  const [filters, setFilters] = useState({ author: '', year: '', language: '' });
  const [results, setResults] = useState([]);
  const [numFound, setNumFound] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1);
  const [selected, setSelected] = useState(null);
  const [noResults, setNoResults] = useState(false);

  const PAGE_SIZE = 12;
  const lastGoodResults = useRef({ results: [], numFound: 0, page: 1 });

  useEffect(() => {
    setPage(1);
  }, [query, filters]);

  async function doSearch(qParam, filtersParam, requestedPage = 1) {
    const currentQuery = qParam ?? query;
    const currentFilters = filtersParam ?? filters;
    const safeQuery = typeof currentQuery === 'string' ? currentQuery.trim() : '';

    if (!safeQuery) {
      setError('‚ö†Ô∏è Please enter a search term.');
      setResults([]);
      setNoResults(false);
      return;
    }

    setError('');
    setNoResults(false);
    setLoading(true);

    let attempt = 0;
    const MAX_ATTEMPTS = 3;
    const RETRY_DELAY = 500;

    while (attempt < MAX_ATTEMPTS) {
      try {
        const data = await searchBooks({
          q: safeQuery,
          author: currentFilters.author?.trim() || '',
          year: currentFilters.year?.trim() || '',
          language: currentFilters.language || '',
          page: requestedPage,
          limit: PAGE_SIZE,
        });

        let docs = Array.isArray(data.docs) ? data.docs : [];

        // ‚úÖ Language filter (lenient)
        if (currentFilters.language) {
          const selectedLang = currentFilters.language.toLowerCase();
          docs = docs.filter((book) => {
            if (Array.isArray(book.language) && book.language.length > 0) {
              return book.language.some((lang) =>
                String(lang).toLowerCase().includes(selectedLang)
              );
            }
            return true;
          });
        }

        // ‚úÖ Auto-retry if docs empty but total > 0
        if (docs.length === 0 && (data.numFound || 0) > 0 && attempt < MAX_ATTEMPTS - 1) {
          attempt++;
          await new Promise((r) => setTimeout(r, RETRY_DELAY * attempt));
          continue;
        }

        // ‚úÖ Successful response
        setResults(docs);
        setNumFound(data.numFound || 0);
        setPage(requestedPage);
        setError('');

        if (docs.length > 0) {
          lastGoodResults.current = { results: docs, numFound: data.numFound || 0, page: requestedPage };
          setNoResults(false);
        } else if ((data.numFound || 0) > 0) {
          setError('üåç No books returned on this page, but results exist. Try retrying the same page.');
        } else {
          setNoResults(true);
        }

        // ‚úÖ Break loop after success
        break;
      } catch (e) {
        console.error('Search error:', e);
        attempt++;

        if (attempt >= MAX_ATTEMPTS) {
          if (lastGoodResults.current.results.length > 0) {
            setError('‚ùå Temporary network error. Showing last results. Retry to fetch fresh data.');
            setResults(lastGoodResults.current.results);
            setNumFound(lastGoodResults.current.numFound);
            setPage(lastGoodResults.current.page);
          } else {
            setError('‚ùå Failed to fetch data. Please check your connection and try again.');
            setResults([]);
            setNumFound(0);
            setPage(1);
          }
        } else {
          await new Promise((r) => setTimeout(r, RETRY_DELAY * attempt));
        }
      } finally {
        setLoading(false);
      }
    }
  }

  // ‚úÖ Pagination setup (must be outside of doSearch)
  const totalPages = Math.max(1, Math.ceil(numFound / PAGE_SIZE));
  const isLastPage = page >= totalPages;

  return (
    <div>
      <SearchBar
        query={query}
        onQueryChange={setQuery}
        filters={filters}
        onFiltersChange={setFilters}
        onSearch={() => doSearch(query, filters, 1)}
        loading={loading}
      />

      {/* ‚ö†Ô∏è Error message + Retry */}
      {error && (
        <div className="notice error">
          {error}
          <button
            className="btn small retry"
            onClick={() => doSearch(query, filters, page)}
            disabled={loading}
          >
            Retry
          </button>
        </div>
      )}

      {/* üîç Loading */}
      {loading && <div className="notice">üîé Searching books...</div>}

      {/* üì≠ No Results */}
      {noResults && !loading && !error && (
        <div className="notice">üì≠ No results found for this search.</div>
      )}

      {/* üìö Results */}
      {!loading && results.length > 0 && (
        <div className="grid">
          {results.map((b) => (
            <BookCard
              key={`${b.key}_${b.cover_i || ''}`}
              book={b}
              onOpen={() => setSelected(b)}
            />
          ))}
        </div>
      )}

      {/* ‚è© Pagination */}
      {numFound > 0 && !loading && (
        <div className="pagination">
          <button
            onClick={() => doSearch(query, filters, Math.max(1, page - 1))}
            disabled={loading || page <= 1}
          >
            Prev
          </button>

          <span>
            Page {page} / {totalPages}
          </span>

          <button
            onClick={() => !isLastPage && doSearch(query, filters, page + 1)}
            disabled={loading || isLastPage}
          >
            {loading ? 'Loading...' : 'Next'}
          </button>
        </div>
      )}

      {/* üìò Modal */}
      {selected && (
        <BookDetailsModal book={selected} onClose={() => setSelected(null)} />
      )}
    </div>
  );
}
